import React from "react";
import axios from 'axios'
import { useState } from 'react';
import { useEffect } from 'react';
import '../css/bookSearch.css';
import Noimage from '../image/noImage.png';

const Result = ({book}) =>{
    
   //const [posts, setPosts] = useState([]);
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                            
    /*useEffect(()=>{
        axios.get('https://www.googleapis.com/books/v1/volumes?q=java&maxResults=1')
        .then(res =>setPosts(res.data))
        .catch(error => console.log(error))
    },[])
*/

const [result, setResult] = useState([])
useEffect(() => {
  axios.get('https://www.googleapis.com/books/v1/volumes?q=java&maxResults=12')
    .then(response => {
        console.log(response.data.items)
        setResult(response.data.items)
    })
            
    .catch(error => console.log(error))
}, [])

    return(
        <>
        {/*
        <section id="pickup">
            <div>
                <h2>検索結果</h2>   
                <div id="pickupcontens">
                    <ul>
                        {
                            result.map((books,i) => (
                            <li>
                                <div>
                                    <img src={books.volumeInfo.imageLinks.thumbnail} alt="画像"/>
                                    <h3 className="content-title">{books.volumeInfo.title}</h3>
                                    <p>{books.volumeInfo.authors}</p> 
                                </div>  
                            </li>
                            ))
                        }               
                    </ul>
                </div>
                   
            </div>
        </section>  
         */}
          <div id="book">
            検索結果
          </div>
         <div className="wrap">
             
            {
                result.map((books,i) => (
                    <div className="item">
                    <a href={books.volumeInfo.previewLink}>
                    <img src={books.volumeInfo.imageLinks.thumbnail}/>
                    </a>
                    <h3>{books.volumeInfo.title}</h3>
                    <p>{books.volumeInfo.authors}</p> 
                    </div>
                ))
             }  
         </div>
         </>


    );
};

export default Result;
