import React from "react";
import axios from 'axios'
import { useState } from 'react';
import { useEffect } from 'react';
import '../css/bookSearch.css';
import Noimage from '../image/noImage.png';

const Result = () =>{
    
   //const [posts, setPosts] = useState([]);

    /*useEffect(()=>{
        axios.get('https://www.googleapis.com/books/v1/volumes?q=java&maxResults=1')
        .then(res =>setPosts(res.data))
        .catch(error => console.log(error))
    },[])
*/

const [result, setResult] = useState([])
useEffect(() => {
  axios.get('https://www.googleapis.com/books/v1/volumes?q=java&maxResults=6')
    .then(response => {
        console.log(response.data.items)
        setResult(response.data.items)
    })
            
    .catch(error => console.log(error))
}, [])



    return(
        <section id="pickup">
            <div>
                <h2>検索結果</h2>   
                <div id="pickupcontens">
                    <ul>

                            {
                                result.map((books,i) => (
                            <li>
                                 <img src={books.volumeInfo.imageLinks.thumbnail} alt="画像"/>
                                 <h3 id="content-title">{books.volumeInfo.title}</h3>
                                 <p>{books.volumeInfo.description}</p>    
                            </li>

                                

                                ))}
                        {/** 
                        <li>
                            <img src={Noimage} alt="画像"/>
                            <h3 id="content-title">タイトルタイトル</h3>
                            <p>テキストテキストテキスト</p>    
                        </li>
                        */}


               
                    </ul>
                </div>
            </div>
        </section>

    );
};

export default Result;
