        name: ''
    });
    const handleChange = (name) =>{
       
        setValues(name);
    
    };
    const handleSubmit = () => {
        
        console.log(values);
    };
    
    return(
        <div className="topmain">
            <div>
                <p>BOOK SEARCH</p>
                <br/>
                <input type="text" name="word" size="50"  placeholder="キーワード検索"/>
                <input type="text" name="name" value={values.name} onChange={handleChange('name')} />
                <button onClick={handleSubmit}>検索</button>
            </div>
        </div>
    );

};

export default SearchInput;
